import React, {useState} from 'react';

function Counter() {

    const [counterOne, setCounterOne] = useState(0);
    const [counterTwo, setCounterTwo] = useState(0);

    const incrementOne = () => {
        setCounterOne(counterOne + 1);
    }

    const incrementTwo = () => {
        setCounterTwo(counterTwo + 1);
    }

    const isEven = () => {
        return CounterOne % 2 === 0;
    }

    return (
        <div>
           <button onClick={incrementOne}>CoutnerOne {counterOne}</button>
           <span>{isEven() ? 'Even' : 'Two' }</span>
           <button onClick={incrementTwo}>CountetTwo {counterTwo}</button>
        </div>
    );
}

export default Counter;
